        public class Button
        {
            public HTMLElement htmlElement;
            public void Click()
            {
                //for click a HTML button we first need to dispath a mouseup event
                //this is becouse the PS client demands this behaviour
                //possibly to avoid client-injected bots?! It took me 2 days to figure this out ¬¬
                htmlElement.DispatchEvent(new MouseEvent("mouseup"));
                htmlElement.Click();
            }

            public virtual void SetUpHtmlElement(HTMLElement p_element)
            {
                htmlElement = p_element;
            }

            protected NodeList GetToolTipData()
            {
                //the only way for us to access extra data about a button is to hover over it with the mouse
                //so we simulate this behaviour
                htmlElement.DispatchEvent(new MouseEvent("mouseover"));

                var __tooltip = Document.GetElementsByClassName("tooltip")[0];
                return __tooltip.ChildNodes;
            }
        }

        public class SkillButton : Button
        {
            public string skillName;
            public SkillType skillType;
            public PokemonType pokemonType;
            public int basePower;
            public int accuracy;

            public override void SetUpHtmlElement(HTMLElement p_element)
            {
                //we create a basic skill btn
                //we also scan the tooltips of this button to get extra data of it
                base.SetUpHtmlElement(p_element);

                var __tooltipNodes = base.GetToolTipData();
                var __h2Nodes = __tooltipNodes[0].ChildNodes;
                skillName = __h2Nodes[0].NodeValue;
                HTMLElement __skillTypeNode = __h2Nodes[4] as HTMLElement;
                skillType = (SkillType)Enum.Parse(typeof(SkillType), __skillTypeNode.GetAttribute("alt").ToUpper());

                HTMLElement __elementTypeNode = __h2Nodes[2] as HTMLElement;
                pokemonType = (PokemonType)Enum.Parse(typeof(PokemonType), __elementTypeNode.GetAttribute("alt").ToUpper());
                if (!int.TryParse((__tooltipNodes[1] as HTMLElement).InnerHTML.Split(":")[1].Replace(" ", ""), out basePower))
                    basePower = -1;
                if (!int.TryParse((__tooltipNodes[2] as HTMLElement).InnerHTML.Split(":")[1].Replace(" ", "").Replace("%", ""), out accuracy))
                    accuracy = -1;
            }
        }

