            public override void SetUpHtmlElement(HTMLElement p_element)
            {
                base.SetUpHtmlElement(p_element);

                pokemonData = new PokemonData(p_element);
            }
        }

        public class PokemonData
        {
            public Sex pokemonSex;
            public string pokemonName;
            public int pokemonLevel;
            public PokemonType pokemonTypeMain;
            public PokemonType pokemonTypeSub;

            public PokemonData(HTMLElement p_element)
            {
                p_element.DispatchEvent(new MouseEvent("mouseover"));

                //recover pokemon data from the tooltip of a pokemon
                //either by hovering the mouse under a team member button or a pokemon in the current battle (enemy pokemon for example)
                //TODO:
                //-fix random problems that occur when the pokemon changes it TYPE during battle

                var __tooltip = Document.GetElementsByClassName("tooltip")[0];
                if (__tooltip != null)
                {
                    var __tooltipNodes = Document.GetElementsByClassName("tooltip")[0].ChildNodes;
                    var __h2Nodes = __tooltipNodes[0].ChildNodes;
                    pokemonName = __h2Nodes[0].NodeValue.Replace(" ", "");

                    var __smallElements = (__tooltipNodes[0] as HTMLElement).GetElementsByTagName("small");

                    var __levelTag = __smallElements[__smallElements.Length-1];
                    if (__levelTag != null)
                        pokemonLevel = int.Parse(__levelTag.InnerHTML.Replace(" ", "").Replace("L", ""));
                    else
                        pokemonLevel = -1;

                    var __imageTags = (__tooltipNodes[0] as HTMLElement).GetElementsByTagName("img");
                    string __sexTag = __imageTags[0].GetAttribute("alt").ToUpper();
                    if (__sexTag == "M")
                        pokemonSex = Sex.MALE;
                    if (__sexTag == "F")
                        pokemonSex = Sex.FEMALE;

                    if (pokemonSex == Sex.UNDEFINED)
                    {
                        pokemonTypeMain = (PokemonType)Enum.Parse(typeof(PokemonType), __imageTags[0].GetAttribute("alt").ToUpper());
                        if (__imageTags.Length < 2)
                            pokemonTypeSub = PokemonType.NONE;
                        else
                            pokemonTypeSub = (PokemonType)Enum.Parse(typeof(PokemonType), __imageTags[1].GetAttribute("alt").ToUpper());
                    }
                    else
                    {
                        pokemonTypeMain = (PokemonType)Enum.Parse(typeof(PokemonType), __imageTags[1].GetAttribute("alt").ToUpper());
                        if (__imageTags.Length < 3)
                            pokemonTypeSub = PokemonType.NONE;
                        else
                            pokemonTypeSub = (PokemonType)Enum.Parse(typeof(PokemonType), __imageTags[2].GetAttribute("alt").ToUpper());
                    }
                }
                //this closes the current tooltip window
                p_element.DispatchEvent(new MouseEvent("mouseup")); 
            }
        }


