        //this method analyse the current game state by
        //crawling its HTML elements and generate a report from it
        public Report Analyse()
        {
            Report __report = new Report();

            //lobby status
            __report.isInLobby = Document.GetElementsByClassName("battle")[0] == null;
            __report.isInBattle = !__report.isInLobby;

            if (__report.isInLobby)
            {
                var __bigButtonToStartNewGame = Document.GetElementsByClassName("button mainmenu1 big")[0];
                __report.startRandomGameBtn = new Button();
                __report.startRandomGameBtn.SetUpHtmlElement(__bigButtonToStartNewGame);

                var __challengeDiv = Document.GetElementsByClassName("challenge")[0];
                __report.isBeingChallenged = __challengeDiv != null;
                if(__report.isBeingChallenged)
                {
                    var __challengeBtns = __challengeDiv.GetElementsByTagName("button");
                    foreach(HTMLElement btn in __challengeBtns)
                    {
                        if(btn.GetAttribute("name") == "acceptChallenge")
                        {
                            __report.acceptChallengeBtn = new Button();
                            __report.acceptChallengeBtn.SetUpHtmlElement(btn);
                        }
                    }
                    if(__report.acceptChallengeBtn == null)
                        __report.isBeingChallenged = false;
                }
            }

            //login data
            __report.userBarDiv = Document.GetElementsByClassName("userbar")[0];
            if(__report.userBarDiv != null)
                __report.loginDiv = __report.userBarDiv.GetElementsByClassName("username")[0];

            __report.isLoggedIn = __report.loginDiv != null;

            if(__report.isLoggedIn)
                __report.username = __report.loginDiv.GetAttribute("data-name").Replace(" ", "");

            if (__report.isInBattle)
            {
                //battle log data
                __report.battleLogDivParent = Document.GetElementsByClassName("battle-log")[0];
                if (__report.battleLogDivParent != null)
                    __report.battleLogDiv = __report.battleLogDivParent.GetElementsByClassName("inner")[0];

                if (__report.battleLogDiv != null)
                {
                    var __turnTagElements = __report.battleLogDiv.GetElementsByTagName("h2");
                    __report.currentTurn = __turnTagElements.Length;


                }


                //skills data
                __report.skillsMenuDiv = Document.GetElementsByClassName("movemenu")[0];
                __report.isSkillsMenuAvailable = __report.skillsMenuDiv != null;
                __report.availableSkillsBtns = new List<SkillButton>();
                if (__report.isSkillsMenuAvailable)
                {
                    var __childElements = __report.skillsMenuDiv.GetElementsByTagName("button");
                    for (int i = 0; i < __childElements.Length; i++)
                        if ((__childElements[i] as HTMLElement).GetAttribute("name") == "chooseMove")
                        {
                            SkillButton __skillBtn = new SkillButton();
                            __skillBtn.SetUpHtmlElement(__childElements[i] as HTMLElement);
                            __report.availableSkillsBtns.Add(__skillBtn);
                        }
                }

                __report.availableSkills = __report.availableSkillsBtns.Count;


                //team data
                __report.teamMenuDiv = Document.GetElementsByClassName("switchmenu")[0];
                __report.isTeamMenuAvailable = __report.teamMenuDiv != null;
                __report.availableTeamMembersBtns = new List<TeamMemberButton>();
                if (__report.isTeamMenuAvailable)
                {
                    var __childBtns = __report.teamMenuDiv.GetElementsByTagName("button");

                    for (int i = 0; i < __childBtns.Length; i++)
                        if ((__childBtns[i] as HTMLElement).GetAttribute("name") == "chooseSwitch")
                        {
                            TeamMemberButton __teamMemberBtn = new TeamMemberButton();
                            __teamMemberBtn.SetUpHtmlElement(__childBtns[i] as HTMLElement);
                            __report.availableTeamMembersBtns.Add(__teamMemberBtn);
                        }
                }

                //pokemon in battle data
                __report.battleDiv = Document.GetElementsByClassName("battle")[0].GetElementsByClassName("innerbattle")[0];
                __report.currentEnemyPokemonStatusBar = __report.battleDiv.GetElementsByClassName("statbar lstatbar")[0];
                __report.currentMyPokemonStatusBar = __report.battleDiv.GetElementsByClassName("statbar rstatbar")[0];

                var __currentPokemonInBattleMaskDivs = Document.GetElementsByClassName("foehint")[0].GetElementsByTagName("div");

                __report.isMyPokemonSpawned = __report.currentMyPokemonStatusBar != null;
                __report.isEnemyPokemonSpawned = __report.currentEnemyPokemonStatusBar != null;

                if (__report.isMyPokemonSpawned)
                {
                    __report.currentMyPokemonHP = int.Parse(__report.currentMyPokemonStatusBar.GetElementsByClassName("hptext")[0].InnerHTML.Replace("%", ""));
                    __report.currentMyPokemon = new PokemonData(__currentPokemonInBattleMaskDivs[3]);
                }  
                else
                    __report.currentMyPokemonHP = -1;

                if (__report.isEnemyPokemonSpawned)
                {
                    __report.currentEnemyPokemonHP = int.Parse(__report.currentEnemyPokemonStatusBar.GetElementsByClassName("hptext")[0].InnerHTML.Replace("%", ""));
                    __report.currentEnemyPokemon = new PokemonData(__currentPokemonInBattleMaskDivs[2]);
                }
                else
                    __report.currentEnemyPokemonHP = -1;
                
                __report.availableTeamMembers = __report.availableTeamMembersBtns.Count;

                //timer data
                var __timerBtn = Document.GetElementsByClassName("setTimer")[0];
                __report.isTimerBtnAvailable = __timerBtn != null;

                if (__report.isTimerBtnAvailable)
                {
                    __report.isTimerOn = __timerBtn.GetAttribute("value") == "on";
                    __report.timerBtn = new Button();
                    __report.timerBtn.SetUpHtmlElement(__timerBtn);
                }

                //end game data
                var __closeGameBtn = Document.GetElementsByClassName("closebutton")[0];
                __report.closeGameBtn = new Button();
                __report.closeGameBtn.SetUpHtmlElement(__closeGameBtn);
                __report.isGameOver = false;
                if (__report.battleLogDiv != null)
                {
                    var __allDivsInBattleLog = __report.battleLogDiv.GetElementsByTagName("div");
                    foreach(HTMLElement div in __allDivsInBattleLog)
                    {
                        string __divContent = div.InnerHTML;
                        if (__divContent.ToLower().EndsWith("won the battle!"))
                            __report.isGameOver = true;
                    }
                }
            }
            
            //done
            return __report;
        }

        //over load of SimulateCombatResult(PokemonType,PokemonType,PokemonType)
        public static PokemonTypeEffectivenessType SimulateCombatResult(PokemonData p_attacker, PokemonData p_defender)
        {
            PokemonTypeEffectivenessType __toReturn = SimulateCombatResult(p_attacker.pokemonTypeMain, p_defender.pokemonTypeMain, p_defender.pokemonTypeSub);
            if (p_attacker == null || p_defender == null)
                return __toReturn;

            if (p_attacker.pokemonTypeSub != PokemonType.NONE)
            {
                PokemonTypeEffectivenessType __subResult = SimulateCombatResult(p_attacker.pokemonTypeSub, p_defender.pokemonTypeMain, p_defender.pokemonTypeSub);
                if (__subResult == PokemonTypeEffectivenessType.SUPER_EFFECTIVE || __subResult == PokemonTypeEffectivenessType.NO_EFFECT)
                    __toReturn = __subResult;
            }

            return __toReturn;
        }

        //simulate a the result of an attack fo a given element against a pokemon with the given defensive elements
        //it uses the "big data" region of this file to "know" what to return
        //the table was gattered from a public site.
        public static PokemonTypeEffectivenessType SimulateCombatResult(PokemonType p_attacker, PokemonType p_mainDefender, PokemonType p_subDefender)
        {
            PokemonTypeEffectivenessType __toReturn = PokemonTypeEffectivenessType.NORMAL_EFFECTIVENESS;

            int __attacker = (int)p_attacker;
            int __mainDefender = (int)p_mainDefender;
            int __subDefender = (int)p_subDefender;

            for(int i = 0; (4*i) < pokeTypesEffectTable.Length; i ++)
            {
                int __offset = 4 * i;
                if(pokeTypesEffectTable[__offset] == __attacker && pokeTypesEffectTable[__offset+1] == __mainDefender && pokeTypesEffectTable[__offset+2] == __subDefender)
                {
                    __toReturn = (PokemonTypeEffectivenessType)pokeTypesEffectTable[__offset + 3];
                    break;
                }
            }

            return __toReturn;
        } 
    }
